import React, { useState, useRef, useEffect } from 'react';
import { io } from 'socket.io-client';
import './Sender.css';
import { QRCodeCanvas } from 'qrcode.react';
import {
  generateKeyPair,
  exportPublicKey,
  importPublicKey,
  deriveAESKey,
  encryptData
} from './cryptoUtils';


function generateId() {
  return Math.random().toString(36).slice(2, 8).toUpperCase();
}

export default function Sender() {
  const [id] = useState(generateId());
  const [receiverId, setReceiverId] = useState('');
  const [status, setStatus] = useState('Idle');
  const [showEncrypted, setShowEncrypted] = useState(false);
  const [chunks, setChunks] = useState([]);
  const pc = useRef(null);
  const socket = useRef(null);
  const dc = useRef(null);
  const fileReader = useRef(null);
  const aesKey = useRef(null);
  const localKeyPair = useRef(null);

  const baseUrl = window.location.origin;
  const shareUrl = `${baseUrl}/receiver?id=${id}`;

  useEffect(() => {
    socket.current = io('https://192.168.168.36:8080');
    socket.current.on('connect', () => {
      socket.current.emit('register', id);
      setStatus(`Registered as ${id}`);
    });

    socket.current.on('signal', async (data) => {
      if (data.from !== receiverId) return;

      if (data.signal.publicKey) {
        const receiverPub = await importPublicKey(data.signal.publicKey);
        aesKey.current = await deriveAESKey(localKeyPair.current.privateKey, receiverPub);
      }

      if (data.signal.sdp) {
        await pc.current.setRemoteDescription(new RTCSessionDescription(data.signal));
        if (data.signal.type === 'offer') {
          const answer = await pc.current.createAnswer();
          await pc.current.setLocalDescription(answer);
          socket.current.emit('signal', {
            target: receiverId,
            signal: pc.current.localDescription,
          });
        }
      } else if (data.signal.candidate) {
        await pc.current.addIceCandidate(new RTCIceCandidate(data.signal.candidate));
      }
    });

    return () => socket.current.disconnect();
  }, [id, receiverId]);

  const startConnection = async () => {
    pc.current = new RTCPeerConnection();
    localKeyPair.current = await generateKeyPair();
    const pub = await exportPublicKey(localKeyPair.current.publicKey);

    socket.current.emit('signal', {
      target: receiverId,
      signal: { publicKey: pub },
    });

    dc.current = pc.current.createDataChannel('fileTransfer');

    dc.current.onopen = () => setStatus('Data channel open. Ready to send file.');
    dc.current.onclose = () => setStatus('Data channel closed.');
    dc.current.onerror = (error) => console.error('Data channel error:', error);

    pc.current.onicecandidate = (event) => {
      if (event.candidate) {
        socket.current.emit('signal', {
          target: receiverId,
          signal: { candidate: event.candidate },
        });
      }
    };

    const offer = await pc.current.createOffer();
    await pc.current.setLocalDescription(offer);
    socket.current.emit('signal', {
      target: receiverId,
      signal: pc.current.localDescription,
    });
    setStatus('Offer sent.');
  };

  const sendFile = (file) => {
    const chunkSize = 16384;
    let offset = 0;
    fileReader.current = new FileReader();

    fileReader.current.onload = async (e) => {
      const chunk = e.target.result;
      const { encrypted, iv } = await encryptData(aesKey.current, chunk);

      if (showEncrypted) {
        setChunks(prev => [
          ...prev,
          {
            iv: Array.from(new Uint8Array(iv)).map(b => b.toString(16).padStart(2, '0')).join(''),
            data: Array.from(new Uint8Array(encrypted)).map(b => b.toString(16).padStart(2, '0')).join('')
          }
        ]);
      }

      dc.current.send(iv);
      dc.current.send(encrypted);

      offset += chunk.byteLength;
      setStatus(`Sending: ${((offset / file.size) * 100).toFixed(2)}%`);

      if (offset < file.size) readSlice(offset);
      else dc.current.send(JSON.stringify({ done: true, fileName: file.name }));
    };

    const readSlice = (o) => {
      const slice = file.slice(o, o + chunkSize);
      fileReader.current.readAsArrayBuffer(slice);
    };

    readSlice(0);
  };

  return (
    <div className="sender-container">
      <div className="sender-header">
        <h2>Secure File Sender</h2>
        <span className="sender-id">ID: {id}</span>
      </div>

      <div className="sender-controls">
        <div className="input-group">
          <input
            type="text"
            className="sender-input"
            placeholder="Enter Receiver ID"
            value={receiverId}
            onChange={e => setReceiverId(e.target.value.toUpperCase())}
          />
        </div>

        <button
          className="sender-button"
          onClick={startConnection}
          disabled={!receiverId}
        >
          Connect to Receiver
        </button>

        <div className="file-input-container">
          <label className="file-input-label">
            <input
              type="file"
              className="file-input"
              onChange={e => sendFile(e.target.files[0])}
              disabled={!dc.current || dc.current.readyState !== 'open'}
            />
            {dc.current && dc.current.readyState === 'open'
              ? 'Click to select a file'
              : 'Connect to a receiver first'}
          </label>
        </div>

        <div className="status-container">
          <div className="status-indicator"></div>
          <span>{status}</span>
        </div>

        <div className="qr-section">
          <p>Share this link or QR code with the receiver:</p>
          <input type="text" readOnly value={shareUrl} className="share-url" />
          <QRCodeCanvas value={sessionLink} size={200} />
        </div>

        <div style={{ marginTop: '1rem' }}>
          <label>
            <input
              type="checkbox"
              checked={showEncrypted}
              onChange={e => setShowEncrypted(e.target.checked)}
            />
            Show Encrypted Chunks
          </label>
          {showEncrypted && (
            <div className="encrypted-chunks">
              <h4>Encrypted Chunks:</h4>
              <pre>{JSON.stringify(chunks, null, 2)}</pre>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
