import React, { useState, useRef, useEffect } from 'react';
import { QRCodeCanvas } from 'qrcode.react';

import { v4 as uuidv4 } from 'uuid';

const SIGNALING_SERVER_URL = 'ws://localhost:8080';

function App() {
  const [ws, setWs] = useState(null);
  const [peerConnection, setPeerConnection] = useState(null);
  const [localId, setLocalId] = useState('');
  const [remoteId, setRemoteId] = useState('');
  const [role, setRole] = useState('');
  const [qrValue, setQrValue] = useState('');
  const [messages, setMessages] = useState([]);
  const [file, setFile] = useState(null);
  const dataChannelRef = useRef(null);

  useEffect(() => {
    if (!localId) return;
    const socket = new WebSocket(SIGNALING_SERVER_URL);
    socket.onopen = () => {
      socket.send(JSON.stringify({ type: 'register', id: localId }));
      logMsg('Connected to signaling server');
    };
    socket.onmessage = async (event) => {
      const msg = JSON.parse(event.data);
      if (msg.from === localId) return;

      if (msg.type === 'offer' && role === 'receiver') {
        await handleOffer(msg);
      } else if (msg.type === 'answer' && role === 'sender') {
        await handleAnswer(msg);
      } else if (msg.type === 'ice-candidate') {
        if (peerConnection) {
          try {
            await peerConnection.addIceCandidate(msg.data);
            logMsg('Added ICE candidate');
          } catch (e) {
            console.error(e);
          }
        }
      }
    };
    setWs(socket);

    return () => {
      socket.close();
    };
  }, [localId, role, peerConnection]);

  const createPeerConnection = () => {
    const pc = new RTCPeerConnection({
      iceServers: [{ urls: 'stun:stun.l.google.com:19302' }],
    });

    pc.onicecandidate = (event) => {
      if (event.candidate) {
        ws.send(
          JSON.stringify({
            type: 'ice-candidate',
            from: localId,
            to: remoteId,
            data: event.candidate,
          })
        );
        logMsg('Sent ICE candidate');
      }
    };

    pc.onconnectionstatechange = () => {
      logMsg('Connection state: ' + pc.connectionState);
    };

    return pc;
  };

  const onFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const startSender = async () => {
    if (!remoteId) {
      alert('Please enter receiver ID');
      return;
    }
    const pc = createPeerConnection();

    const dc = pc.createDataChannel('fileChannel');
    dataChannelRef.current = dc;

    dc.onopen = () => {
      logMsg('Data channel open');
      if (file) {
        sendFile(file);
      }
    };
    dc.onclose = () => logMsg('Data channel closed');
    dc.onerror = (err) => logMsg('Data channel error: ' + err);

    const offer = await pc.createOffer();
    await pc.setLocalDescription(offer);

    ws.send(
      JSON.stringify({
        type: 'offer',
        from: localId,
        to: remoteId,
        data: offer,
      })
    );

    setPeerConnection(pc);
    logMsg('Offer sent');
  };

  const handleOffer = async (msg) => {
    const pc = createPeerConnection();

    pc.ondatachannel = (event) => {
      const dc = event.channel;
      dataChannelRef.current = dc;

      dc.binaryType = 'arraybuffer';

      let receivedBuffers = [];
      dc.onmessage = (e) => {
        receivedBuffers.push(e.data);
        logMsg(`Received chunk (${e.data.byteLength} bytes)`);
      };
      dc.onclose = () => {
        logMsg('Data channel closed, assembling file');
        const received = new Blob(receivedBuffers);
        const a = document.createElement('a');
        a.href = URL.createObjectURL(received);
        a.download = 'received_file';
        a.click();
        receivedBuffers = [];
      };
    };

    await pc.setRemoteDescription(new RTCSessionDescription(msg.data));
    const answer = await pc.createAnswer();
    await pc.setLocalDescription(answer);

    ws.send(
      JSON.stringify({
        type: 'answer',
        from: localId,
        to: msg.from,
        data: answer,
      })
    );

    setPeerConnection(pc);
    setRemoteId(msg.from);
    logMsg('Answer sent');
  };

  const handleAnswer = async (msg) => {
    if (!peerConnection) return;
    await peerConnection.setRemoteDescription(new RTCSessionDescription(msg.data));
    logMsg('Answer received and set');
  };

  const sendFile = (file) => {
    const chunkSize = 16384;
    let offset = 0;
    const dc = dataChannelRef.current;

    const reader = new FileReader();

    reader.onload = (e) => {
      if (!dc || dc.readyState !== 'open') {
        logMsg('Data channel not open');
        return;
      }

      dc.send(e.target.result);

      offset += e.target.result.byteLength;
      logMsg(`Sent ${offset} bytes`);

      if (offset < file.size) {
        readSlice(offset);
      } else {
        logMsg('File sent!');
      }
    };

    const readSlice = (o) => {
      const slice = file.slice(o, o + chunkSize);
      reader.readAsArrayBuffer(slice);
    };

    readSlice(0);
  };

  const logMsg = (msg) => {
    setMessages((msgs) => [...msgs, msg]);
    console.log(msg);
  };

  useEffect(() => {
    setLocalId(uuidv4());
  }, []);

  useEffect(() => {
    if (localId) setQrValue(localId);
  }, [localId]);

  return (
    <div style={{ padding: 20 }}>
      <h2>Secure P2P File Transfer</h2>

      {!role && (
        <div>
          <button onClick={() => setRole('sender')}>I'm Sender</button>{' '}
          <button onClick={() => setRole('receiver')}>I'm Receiver</button>
        </div>
      )}

      {role && (
        <>
          <p>
            Your ID (share this with other): <b>{localId}</b>
          </p>
          <QRCode value={qrValue} size={128} />

          <div style={{ marginTop: 20 }}>
            {role === 'sender' && (
              <>
                <input
                  type="text"
                  placeholder="Enter receiver ID"
                  value={remoteId}
                  onChange={(e) => setRemoteId(e.target.value)}
                  style={{ width: '300px' }}
                />
                <br />
                <input type="file" onChange={onFileChange} />
                <br />
                <button onClick={startSender} disabled={!file || !remoteId}>
                  Send File
                </button>
              </>
            )}

            {role === 'receiver' && (
              <p>Waiting for connection from sender...</p>
            )}
          </div>

          <div style={{ marginTop: 30, maxHeight: 200, overflowY: 'auto', border: '1px solid #ccc', padding: 10 }}>
            <h4>Logs:</h4>
            {messages.map((m, i) => (
              <div key={i}>{m}</div>
            ))}
          </div>
        </>
      )}
    </div>
  );
}

export default App;
