// src/Receiver.js
import React, { useEffect, useRef, useState } from 'react';

const SIGNAL_SERVER = 'ws://localhost:8080';

function Receiver() {
  const [myId] = useState(() => Math.random().toString(36).slice(2, 8));
  const [senderId, setSenderId] = useState('');
  const ws = useRef(null);
  const pc = useRef(null);
  const dataChannel = useRef(null);
  const receivedChunks = useRef([]);

  useEffect(() => {
    ws.current = new WebSocket(SIGNAL_SERVER);

    ws.current.onopen = () => {
      ws.current.send(JSON.stringify({ register: myId }));
      console.log('Receiver connected. My ID:', myId);
    };

    ws.current.onmessage = async (msg) => {
      const data = JSON.parse(msg.data);

      if (data.type === 'offer') {
        pc.current = new RTCPeerConnection({ iceServers: [{ urls: 'stun:stun.l.google.com:19302' }] });

        pc.current.ondatachannel = (event) => {
          dataChannel.current = event.channel;
          dataChannel.current.binaryType = 'arraybuffer';

          dataChannel.current.onmessage = (e) => {
            receivedChunks.current.push(e.data);
          };

          dataChannel.current.onclose = () => {
            const blob = new Blob(receivedChunks.current);
            const url = URL.createObjectURL(blob);
            const a = document.createElement('a');
            a.href = url;
            a.download = 'received_file'; // you can customize file name here
            a.click();
            URL.revokeObjectURL(url);
            console.log('File downloaded successfully.');
            receivedChunks.current = []; // reset for next file
          };
        };

        pc.current.onicecandidate = (e) => {
          if (e.candidate) {
            ws.current.send(JSON.stringify({
              from: myId,
              to: data.from,
              type: 'candidate',
              candidate: e.candidate
            }));
          }
        };

        await pc.current.setRemoteDescription(new RTCSessionDescription(data.sdp));
        const answer = await pc.current.createAnswer();
        await pc.current.setLocalDescription(answer);

        ws.current.send(JSON.stringify({
          from: myId,
          to: data.from,
          type: 'answer',
          sdp: answer
        }));

        setSenderId(data.from);

      } else if (data.type === 'candidate') {
        if (pc.current) {
          await pc.current.addIceCandidate(data.candidate);
        }
      }
    };

    return () => {
      if (ws.current) ws.current.close();
      if (pc.current) pc.current.close();
      if (dataChannel.current) dataChannel.current.close();
    };
  }, [myId]);

  return (
    <div style={{ padding: 30 }}>
      <h2>Receiver Mode</h2>
      <p>Your ID: <b>{myId}</b></p>
      <p>Waiting for sender...</p>
      {senderId && <p>Connected to: <b>{senderId}</b></p>}
    </div>
  );
}

export default Receiver;
