import React, { useState, useRef, useEffect } from 'react';
import { io } from 'socket.io-client';

function generateId() {
  return Math.random().toString(36).slice(2, 8).toUpperCase();
}

export default function Receiver() {
  const [id] = useState(generateId());
  const [senderId, setSenderId] = useState('');
  const [status, setStatus] = useState('Idle');
  const pc = useRef(null);
  const socket = useRef(null);
  const dc = useRef(null);
  const receivedBuffers = useRef([]);

 useEffect(() => {
  socket.current = io('https://172.17.3.195:8080', {
    transports: ['websocket'],
    rejectUnauthorized: false,
  });

  socket.current.on('connect', () => {
    console.log('Connected with socket id:', socket.current.id);
    socket.current.emit('register', id); // <-- emit string id, not object
    setStatus(`Registered as ${id}`);
  });

  socket.current.on('signal', async (data) => {
    console.log('Signal received:', data);

    if (data.from !== senderId) {
      console.log('Signal ignored from', data.from);
      return;
    }

    const signal = data.signal;

    if (signal.sdp) {
      await pc.current.setRemoteDescription(new RTCSessionDescription(signal));
      if (signal.type === 'offer') {
        const answer = await pc.current.createAnswer();
        await pc.current.setLocalDescription(answer);
        socket.current.emit('signal', {
          target: senderId,
          signal: pc.current.localDescription,
        });
      }
    } else if (signal.candidate) {
      try {
        await pc.current.addIceCandidate(new RTCIceCandidate(signal.candidate));
      } catch (e) {
        console.error('Error adding ICE candidate:', e);
      }
    }
  });

  return () => {
    socket.current.disconnect();
  };
}, [id, senderId]);


  const startConnection = () => {
    pc.current = new RTCPeerConnection();

    pc.current.ondatachannel = (event) => {
      dc.current = event.channel;

      dc.current.onopen = () => setStatus('Data channel open. Ready to receive file.');

      dc.current.onmessage = (event) => {
        if (typeof event.data === 'string') {
          try {
            const msg = JSON.parse(event.data);
            if (msg.done) {
              setStatus('File received completely. Saving...');
              saveFile(msg.fileName || 'received_file');
            }
          } catch {
            // Not JSON, ignore
          }
        } else {
          receivedBuffers.current.push(event.data);
          setStatus(`Receiving... (${receivedBuffers.current.length} chunks)`);
        }
      };

      dc.current.onclose = () => setStatus('Data channel closed.');
    };

    pc.current.onicecandidate = (event) => {
      if (event.candidate) {
        socket.current.emit('signal', {
          target: senderId,
          signal: { candidate: event.candidate },
        });
      }
    };
  };

  const saveFile = (filename) => {
    const blob = new Blob(receivedBuffers.current);
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = filename;
    document.body.appendChild(a);
    a.click();
    setTimeout(() => {
      document.body.removeChild(a);
      URL.revokeObjectURL(url);
    }, 100);
    setStatus('File saved locally!');
    receivedBuffers.current = [];
  };

  return (
    <div>
      <h2>Receiver (Your ID: {id})</h2>
      <input
        type="text"
        placeholder="Enter Sender ID"
        value={senderId}
        onChange={(e) => setSenderId(e.target.value.toUpperCase())}
      />
      <button onClick={startConnection} disabled={!senderId}>Connect</button>
      <p>Status: {status}</p>
    </div>
  );
}
