// Receiver.js
import React, { useState, useRef, useEffect } from 'react';

function generateId() {
  return Math.random().toString(36).slice(2, 8).toUpperCase();
}

export default function Receiver() {
  const [id] = useState(generateId());
  const [senderId, setSenderId] = useState('');
  const [status, setStatus] = useState('Idle');
  const pc = useRef(null);
  const ws = useRef(null);
  const dc = useRef(null);
  const receivedBuffers = useRef([]);
  const fileName = useRef('');

  useEffect(() => {
    ws.current = new WebSocket('WSs://172.17.1.95:8080');

    ws.current.onopen = () => {
      ws.current.send(JSON.stringify({ type: 'register', id }));
      setStatus(`Registered as ${id}`);
    };

    ws.current.onmessage = async (event) => {
      const data = JSON.parse(event.data);
      if (data.type === 'signal' && data.from === senderId) {
        if (data.signal.sdp) {
          await pc.current.setRemoteDescription(new RTCSessionDescription(data.signal));
          if (data.signal.type === 'offer') {
            const answer = await pc.current.createAnswer();
            await pc.current.setLocalDescription(answer);
            ws.current.send(JSON.stringify({
              type: 'signal',
              target: senderId,
              signal: pc.current.localDescription,
            }));
          }
        } else if (data.signal.candidate) {
          try {
            await pc.current.addIceCandidate(new RTCIceCandidate(data.signal.candidate));
          } catch (e) {
            console.error('Error adding ICE candidate:', e);
          }
        }
      }
    };

    return () => ws.current.close();
  }, [id, senderId]);

  const startConnection = () => {
    pc.current = new RTCPeerConnection();

    pc.current.ondatachannel = (event) => {
      dc.current = event.channel;

      dc.current.onopen = () => {
        setStatus('Data channel open. Ready to receive file.');
      };

      dc.current.onmessage = (event) => {
        if (typeof event.data === 'string') {
          try {
            const msg = JSON.parse(event.data);
            if (msg.done) {
              setStatus('File received completely. Saving...');
              saveFile();
            }
          } catch {
            // Not JSON, ignore
          }
        } else {
          receivedBuffers.current.push(event.data);
          setStatus(`Receiving... (${receivedBuffers.current.length} chunks)`);
        }
      };

      dc.current.onclose = () => {
        setStatus('Data channel closed.');
      };
    };

    pc.current.onicecandidate = (event) => {
      if (event.candidate) {
        ws.current.send(JSON.stringify({
          type: 'signal',
          target: senderId,
          signal: { candidate: event.candidate },
        }));
      }
    };
  };

  const saveFile = () => {
    const blob = new Blob(receivedBuffers.current);
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'received_file';  // You could customize or send filename from sender
    document.body.appendChild(a);
    a.click();
    setTimeout(() => {
      document.body.removeChild(a);
      URL.revokeObjectURL(url);
    }, 100);
    setStatus('File saved locally!');
    receivedBuffers.current = [];
  };

  return (
    <div>
      <h2>Receiver (Your ID: {id})</h2>
      <input
        type="text"
        placeholder="Enter Sender ID"
        value={senderId}
        onChange={(e) => setSenderId(e.target.value.toUpperCase())}
      />
      <button onClick={startConnection} disabled={!senderId}>Connect</button>
      <p>Status: {status}</p>
    </div>
  );
}
