import React, { useState } from 'react';
import Sender from './Sender';
import Receiver from './Receiver';
import {
  Button,
  Container,
  Typography,
  Box,
  Paper,
  ThemeProvider,
  createTheme,
  useMediaQuery
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import GetAppIcon from '@mui/icons-material/GetApp';
import './App.css';

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#5862d1',
    },
    secondary: {
      main: '#0277bd',
    },
  },
  typography: {
    fontFamily: '"Segoe UI", "Roboto", "Arial", sans-serif',
    h3: {
      fontWeight: 600,
    },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 8,
          padding: '12px 24px',
        },
      },
    },
  },
});

function App() {
  const [role, setRole] = useState(null);
  const isMobile = useMediaQuery('(max-width:600px)');

  if (role === 'sender') return <Sender />;
  if (role === 'receiver') return <Receiver />;

  return (
    <ThemeProvider theme={theme}>
      <Container maxWidth="sm">
        <Box
          sx={{
            minHeight: '100vh',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <Paper
            elevation={8}
            sx={{
              p: 4,
              width: '100%',
              borderRadius: 3,
              backdropFilter: 'blur(10px)',
              backgroundColor: 'rgba(255, 255, 255, 0.05)',
            }}
          >
            <Typography
              variant="h3"
              component="h1"
              align="center"
              gutterBottom
              sx={{
                mb: 4,
                background: 'linear-gradient(45deg,rgb(255, 255, 255), #0277bd)',
                backgroundClip: 'text',
                WebkitBackgroundClip: 'text',
                color: 'transparent',
              }}
            >
              Secure P2P Transfer
            </Typography>

            <Box
              sx={{
                display: 'flex',
                flexDirection: isMobile ? 'column' : 'row',
                gap: 2,
                justifyContent: 'center',
              }}
            >
              <Button
                variant="contained"
                size="large"
                startIcon={<SendIcon />}
                onClick={() => setRole('sender')}
                fullWidth={isMobile}
                sx={{ flex: isMobile ? 1 : '0 1 200px' }}
              >
                Send Files
              </Button>

              <Button
                variant="contained"
                size="large"
                startIcon={<GetAppIcon />}
                onClick={() => setRole('receiver')}
                fullWidth={isMobile}
                sx={{ 
                  flex: isMobile ? 1 : '0 1 200px',
                  background: 'linear-gradient(45deg, #0277bd, #5862d1)',
                }}
              >
                Receive Files
              </Button>
            </Box>
          </Paper>
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
