// src/Sender.js
import React, { useEffect, useRef, useState } from 'react';

const SIGNAL_SERVER = 'ws://localhost:8080';

function Sender() {
  const [myId] = useState(() => Math.random().toString(36).slice(2, 8));
  const [receiverId, setReceiverId] = useState('');
  const [file, setFile] = useState(null);
  const ws = useRef(null);
  const pc = useRef(null);
  const dataChannel = useRef(null);

  useEffect(() => {
    ws.current = new WebSocket(SIGNAL_SERVER);
    ws.current.onopen = () => {
      ws.current.send(JSON.stringify({ register: myId }));
      console.log('Sender connected. My ID:', myId);
    };

    ws.current.onmessage = async (msg) => {
      const data = JSON.parse(msg.data);
      if (data.type === 'answer') {
        await pc.current.setRemoteDescription(new RTCSessionDescription(data.sdp));
      } else if (data.type === 'candidate') {
        await pc.current.addIceCandidate(data.candidate);
      }
    };

    return () => {
      if (ws.current) ws.current.close();
      if (pc.current) pc.current.close();
      if (dataChannel.current) dataChannel.current.close();
    };
  }, [myId]);

  const connectAndSend = async () => {
    if (!file || !receiverId) return;

    pc.current = new RTCPeerConnection({ iceServers: [{ urls: 'stun:stun.l.google.com:19302' }] });

    dataChannel.current = pc.current.createDataChannel('file');

    dataChannel.current.binaryType = 'arraybuffer';

    dataChannel.current.onopen = () => {
      console.log('Data channel opened, starting file transfer...');
      sendFile(file);
    };

    dataChannel.current.onclose = () => {
      console.log('Data channel closed.');
    };

    pc.current.onicecandidate = (e) => {
      if (e.candidate) {
        ws.current.send(JSON.stringify({
          from: myId,
          to: receiverId,
          type: 'candidate',
          candidate: e.candidate
        }));
      }
    };

    const offer = await pc.current.createOffer();
    await pc.current.setLocalDescription(offer);
    ws.current.send(JSON.stringify({
      from: myId,
      to: receiverId,
      type: 'offer',
      sdp: offer
    }));
  };

const sendFile = (file) => {
  const chunkSize = 16 * 1024; // 16 KB chunks
  let offset = 0;

  const reader = new FileReader();

  reader.onload = (e) => {
    if (dataChannel.current.bufferedAmount > 16 * chunkSize) {
      setTimeout(() => {
        sendChunk(e.target.result);
      }, 100);
    } else {
      sendChunk(e.target.result);
    }
  };

  const sendChunk = (chunk) => {
    dataChannel.current.send(chunk);
    offset += chunk.byteLength;
    if (offset < file.size) {
      readSlice(offset);
    } else {
      console.log('File sent completely, closing data channel...');
      dataChannel.current.close();
    }
  };

  const readSlice = (o) => {
    const slice = file.slice(o, o + chunkSize);
    reader.readAsArrayBuffer(slice);
  };

  readSlice(0);
};



  return (
    <div style={{ padding: 30 }}>
      <h2>Sender Mode</h2>
      <p>Your ID: <b>{myId}</b></p>
      <input
        placeholder="Receiver ID"
        value={receiverId}
        onChange={(e) => setReceiverId(e.target.value)}
      />
      <br /><br />
      <input
        type="file"
        onChange={(e) => setFile(e.target.files[0])}
      />
      <br /><br />
      <button
        onClick={connectAndSend}
        disabled={!file || !receiverId}
      >
        Connect & Send
      </button>
    </div>
  );
}

export default Sender;
