// src/App.js
import React, { useRef, useState } from 'react';
import './App.css';
import { v4 as uuidv4 } from 'uuid';
import { QRCodeCanvas } from 'qrcode.react';



const App = () => {
  const [isSender, setIsSender] = useState(null);
  const [peerId, setPeerId] = useState('');
  const [generatedId, setGeneratedId] = useState(uuidv4());
  const [file, setFile] = useState(null);
  const [receivedFile, setReceivedFile] = useState(null);
  const [logs, setLogs] = useState([]);

  const peerConnection = useRef(null);
  const dataChannel = useRef(null);
  const signalingSocket = useRef(null);

  const appendLog = (msg) => setLogs((prev) => [...prev, msg]);

  const connectToSignalingServer = () => {
    signalingSocket.current = new WebSocket('ws://localhost:8080');
    signalingSocket.current.onopen = () => appendLog('Connected to signaling server');

    signalingSocket.current.onmessage = async (message) => {
      const data = JSON.parse(message.data);

      switch (data.type) {
        case 'offer':
          await handleOffer(data.offer, data.from);
          break;
        case 'answer':
          await handleAnswer(data.answer);
          break;
        case 'ice-candidate':
          if (data.candidate) {
            try {
              await peerConnection.current.addIceCandidate(new RTCIceCandidate(data.candidate));
              appendLog('ICE candidate added');
            } catch (err) {
              appendLog(`Failed to add ICE candidate: ${err.message}`);
            }
          }
          break;
        default:
          break;
      }
    };
  };

  const createPeerConnection = () => {
    peerConnection.current = new RTCPeerConnection();

    peerConnection.current.onicecandidate = (event) => {
      if (event.candidate) {
        signalingSocket.current.send(JSON.stringify({
          type: 'ice-candidate',
          candidate: event.candidate,
          to: peerId,
        }));
        appendLog('Sent ICE candidate');
      }
    };

    peerConnection.current.onconnectionstatechange = () => {
      appendLog(`Connection state: ${peerConnection.current.connectionState}`);
    };

    peerConnection.current.ondatachannel = (event) => {
      dataChannel.current = event.channel;

      dataChannel.current.onopen = () => appendLog('Data channel open');
      dataChannel.current.onmessage = (event) => {
        const blob = new Blob([event.data]);
        setReceivedFile(URL.createObjectURL(blob));
        appendLog('File received');
      };
    };
  };

  const startSender = async () => {
    setIsSender(true);
    connectToSignalingServer();
    createPeerConnection();

    dataChannel.current = peerConnection.current.createDataChannel('fileTransfer');

    dataChannel.current.onopen = () => appendLog('Data channel open');

    const offer = await peerConnection.current.createOffer();
    await peerConnection.current.setLocalDescription(offer);

    signalingSocket.current.send(JSON.stringify({
      type: 'offer',
      offer,
      from: generatedId,
      to: peerId,
    }));

    appendLog('Offer sent');
  };

  const handleOffer = async (offer, from) => {
    setPeerId(from);
    createPeerConnection();
    await peerConnection.current.setRemoteDescription(new RTCSessionDescription(offer));

    const answer = await peerConnection.current.createAnswer();
    await peerConnection.current.setLocalDescription(answer);

    signalingSocket.current.send(JSON.stringify({
      type: 'answer',
      answer,
      to: from,
    }));

    appendLog('Answer sent');
  };

  const handleAnswer = async (answer) => {
    await peerConnection.current.setRemoteDescription(new RTCSessionDescription(answer));
    appendLog('Answer received and set');
  };

  const sendFile = () => {
    if (dataChannel.current && dataChannel.current.readyState === 'open') {
      const reader = new FileReader();
      reader.onload = () => {
        dataChannel.current.send(reader.result);
        appendLog('File sent');
      };
      reader.readAsArrayBuffer(file);
    } else {
      appendLog('Data channel not open');
    }
  };

  return (
    <div className="App">
      <h1>Secure P2P File Transfer</h1>

      {isSender === null && (
        <>
          <button onClick={() => setIsSender(true)}>I'm the Sender</button>
          <button onClick={() => setIsSender(false)}>I'm the Receiver</button>
        </>
      )}

      {isSender && (
        <>
          <p>Your ID (share this with receiver): <b>{generatedId}</b></p>
          <QRCodeCanvas value={generatedId} />
          <input
            type="text"
            placeholder="Enter receiver ID"
            value={peerId}
            onChange={(e) => setPeerId(e.target.value)}
          />
          <input type="file" onChange={(e) => setFile(e.target.files[0])} />
          <button onClick={startSender}>Start</button>
          <button onClick={sendFile}>Send File</button>
        </>
      )}

      {isSender === false && (
        <>
          <p>Your ID (share this with sender): <b>{generatedId}</b></p>
          <QRCodeCanvas value={generatedId} />

          <input
            type="text"
            placeholder="Enter sender ID"
            value={peerId}
            onChange={(e) => setPeerId(e.target.value)}
          />
          <button onClick={connectToSignalingServer}>Start</button>
          {receivedFile && (
            <a href={receivedFile} download="received_file">Download Received File</a>
          )}
        </>
      )}

      <div style={{ textAlign: 'left', marginTop: 20 }}>
        <h3>Logs:</h3>
        <pre>{logs.join('\n')}</pre>
      </div>
    </div>
  );
};

export default App;
